import itertools
import hashlib
import time

# Define the password hash function
def hash_password(password):
    return hashlib.sha256(password.encode()).hexdigest()

# Define the password cracking functions
def brute_force_attack(password_hash, length):
    for i in range(2**length):
        password = ''.join(chr(c) for c in itertools.cycle('abcdefghijklmnopqrstuvwxyz'))
        if hash_password(password) == password_hash:
            return password
    return None

def dictionary_attack(password_hash, dictionary_file):
    with open(dictionary_file, 'r') as f:
        dictionary = f.read().splitlines()
    for word in dictionary:
        if hash_password(word) == password_hash:
            return word
    return None

def hybrid_attack(password_hash, length, dictionary_file):
    for i in range(2**length):
        password = ''.join(chr(c) for c in itertools.cycle('abcdefghijklmnopqrstuvwxyz'))
        if hash_password(password) == password_hash:
            return password
    with open(dictionary_file, 'r') as f:
        dictionary = f.read().splitlines()
    for word in dictionary:
        if hash_password(word) == password_hash:
            return word
    return None

# Example usage
password_hash = "sha256:...your_password_hash_here..."
length = 8  # length of the password
dictionary_file = "path/to/your/dictionary.txt"

start_time = time.time()
cracked_password = brute_force_attack(password_hash, length)
if cracked_password:
    print(f"Brute-force attack cracked password: {cracked_password}")
else:
    print("Brute-force attack failed")

start_time = time.time()
cracked_password = dictionary_attack(password_hash, dictionary_file)
if cracked_password:
    print(f"Dictionary attack cracked password: {cracked_password}")
else:
