#include <iostream>
#include <string>

class Vote {
public:
    std::string candidate;
    int count;
};

int main() {
    std::vector<Vote> votes;
    std::string candidateName;
    int numVotes;

    // Get user input for candidate name and number of votes
    std::cout << "Enter candidate name: ";
    std::cin >> candidateName;
    std::cout << "Enter number of votes: ";
    std::cin >> numVotes;

    // Create a new vote object and add it to the vector
    Vote v;
    v.candidate = candidateName;
    v.count = numVotes;
    votes.push_back(v);

    // Print out the current vote count
    for (auto &v : votes) {
        std::cout << v.candidate << ": " << v.count << std::endl;
    }

    return 0;
}
```
