import csv
import logging
import requests

def collect_user_data(api_url, api_token):
    headers = {
        "Authorization": f"Bearer {api_token}"
    }
    try:
        response = requests.get(api_url, headers=headers)
        return response.json()
    except requests.exceptions.RequestException as e:
        logging.error(f"Error collecting user data: {e}")

def export_to_csv(file_name, user_data):
    with open(file_name, "w", newline="") as csvfile:
        writer = csv.writer(csvfile)
        writer.writerow(["User ID", "Username", "Email"])
        for user in user_data:
            writer.writerow([user["id"], user["username"], user["email"]])

api_url = "https://api.example.com/users"
api_token = "your_api_token_here"
user_data = collect_user_data(api_url, api_token)
if user_data is not None:
    export_to_csv("user_data.csv", user_data)

