import random

class Song:
    def __init__(self, title, artist, themes):
        self.title = title
        self.artist = artist
        self.themes = themes

    def __str__(self):
        return f"{self.title} by {self.artist} - {self.themes}"

class MusicLibrary:
    def __init__(self):
        self.songs = {}

    def add_song(self, song):
        self.songs[song.title] = song

    def shuffle_songs(self):
        return list(self.songs.values())

    def organize_songs_by_theme(self):
        themes = {}
        for song in self.songs.values():
            for theme in song.themes.split(","):
                if theme not in themes:
                    themes[theme] = []
                themes[theme].append(song)
        return themes

library = MusicLibrary()

while True:
    print("Options:")
    print("1. Add song")
    print("2. Shuffle songs")
    print("3. Organize songs by theme")
    choice = input("Choose an option:")

    if choice == "1":
        title = input("Enter song title:")
        artist = input("Enter song artist:")
        themes = input("Enter song themes (separated by comma):")
        library.add_song(Song(title, artist, themes))
    elif choice == "2":
        random_songs = library.shuffle_songs()
        for song in random_songs:
            print(song)
    elif choice == "3":
        themes = library.organize_songs_by_theme()
        for theme, songs in themes.items():
            print(theme)
            for song in songs:
                print(song)
            print()
